import React, { useEffect, useMemo, useState } from "react";
import { ThemeProvider } from "./context/ThemeContext";
import Header from "./components/Header";
import CityPicker, { CITIES } from "./components/CityPicker";
import WeatherList from "./components/WeatherList";
import SearchCity from "./components/SearchCity";
import CurrentCard from "./components/CurrentCard";
import { fetchCurrentWeather } from "./services/weather";

export default function App() {
  const [city, setCity] = useState(CITIES[1]); // Monterrey por defecto
  const [current, setCurrent] = useState(null);

  const subtitle = useMemo(
    () => `Pron贸stico por hora 路 ${city.name}`,
    [city.name]
  );

  useEffect(() => {
    let alive = true;
    (async () => {
      try {
        const data = await fetchCurrentWeather(city);
        if (alive) setCurrent(data);
      } catch {
        if (alive) setCurrent(null);
      }
    })();
    return () => { alive = false; };
  }, [city.latitude, city.longitude]);

  return (
    <ThemeProvider>
      <Header title="Clima" subtitle={subtitle} />
      <main className="container row">
        <div className="card">
          <div className="row grid-2">
            <div>
              <h3 style={{ marginTop: 0 }}>Ciudad</h3>
              <div style={{ marginBottom: 8 }}>
                <SearchCity onSelect={setCity} />
              </div>
              <CityPicker value={city} onChange={setCity} />
            </div>
            <div>
              <h3 style={{ marginTop: 0 }}>Coordenadas</h3>
              <div>Lat: {city.latitude.toFixed(4)}</div>
              <div>Lon: {city.longitude.toFixed(4)}</div>
            </div>
          </div>

          <div style={{ marginTop: 16 }}>
            <CurrentCard current={current} />
          </div>
        </div>

        <div className="card">
          <h3 style={{ marginTop: 0 }}>Hoy</h3>
          <WeatherList city={city} />
        </div>
      </main>
    </ThemeProvider>
  );
}
