// src/App.js
import React, { useEffect, useState } from "react";
import { ThemeProvider } from "./context/ThemeContext";
import Header from "./components/Header";
import WeatherList from "./components/WeatherList";
import CurrentCard from "./components/CurrentCard";
import { fetchCurrentWeather } from "./services/weather";
import { CITIES } from "./components/CityPicker"; // seguimos usando CITIES para el valor inicial

export default function App() {
  const [city, setCity] = useState(CITIES[1]); // Monterrey por defecto
  const [current, setCurrent] = useState(null);

  useEffect(() => {
    let alive = true;
    (async () => {
      try {
        const data = await fetchCurrentWeather(city);
        if (alive) setCurrent(data);
      } catch {
        if (alive) setCurrent(null);
      }
    })();
    return () => { alive = false; };
  }, [city.latitude, city.longitude]);

  return (
    <ThemeProvider>
      {/* Título = nombre de la ciudad, sin subtítulo */}
      <Header city={city} onCityChange={setCity} />
      <main className="container row">
        <div className="card">
          <CurrentCard current={current} />
        </div>
        <div className="card">
          <h3 style={{ marginTop: 0 }}>Hoy</h3>
          <WeatherList city={city} />
        </div>
      </main>
    </ThemeProvider>
  );
}
