import React, { useEffect, useState } from "react";
import { ThemeProvider } from "./context/ThemeContext";
import Header from "./components/Header";
import WeatherList from "./components/WeatherList";
import CurrentCard from "./components/CurrentCard";
import { fetchCurrentWeather } from "./services/weather";
import { CITIES } from "./components/CityPicker";
import WeeklyForecast from "./components/WeeklyForecast";

import Login from "./components/Login";

export default function App() {
  const [isLogged, setIsLogged] = useState(false);

  const [city, setCity] = useState(CITIES[1]);
  const [current, setCurrent] = useState(null);

  useEffect(() => {
    if (!isLogged) return;

    let alive = true;
    (async () => {
      try {
        const data = await fetchCurrentWeather(city);
        if (alive) setCurrent(data);
      } catch {
        if (alive) setCurrent(null);
      }
    })();
    return () => {
      alive = false;
    };
  }, [isLogged, city.latitude, city.longitude]);

  if (!isLogged) {
    return (
      <ThemeProvider>
        <Login onLogin={() => setIsLogged(true)} />
      </ThemeProvider>
    );
  }

  return (
    <ThemeProvider>
      <Header city={city} onCityChange={setCity} />

      <main className="container row">
        {/* Tarjeta con los 4 botones de las apps */}
        <div className="card">
          <h3 style={{ marginTop: 0 }}>Apps de los compañeros</h3>
          <div className="row" style={{ gap: "10px", flexWrap: "wrap" }}>
            {/* Cambia las URLs por las de tus compas */}
            <button onClick={() => window.open("https://app1.com", "_blank")}>
              App 1
            </button>
            <button onClick={() => window.open("https://app2.com", "_blank")}>
              App 2
            </button>
            <button onClick={() => window.open("https://app3.com", "_blank")}>
              App 3
            </button>
            <button onClick={() => window.open("https://app4.com", "_blank")}>
              App 4
            </button>
          </div>
        </div>

        {/* Tu app del clima tal cual la tenías */}
        <div className="card">
          <CurrentCard current={current} />
        </div>

        <div className="card">
          <h3 style={{ marginTop: 0 }}>Hoy</h3>
          <WeatherList city={city} />
        </div>

        <div className="card">
          <WeeklyForecast city={city} />
        </div>
      </main>
    </ThemeProvider>
  );
}
