import React, { useEffect, useState } from "react";
import { ThemeProvider } from "./context/ThemeContext";
import Header from "./components/Header";
import WeatherList from "./components/WeatherList";
import CurrentCard from "./components/CurrentCard";
import { fetchCurrentWeather } from "./services/weather";
import { CITIES } from "./components/CityPicker";
import WeeklyForecast from "./components/WeeklyForecast";

// nuevos componentes
import Login from "./components/Login";
import Home from "./components/Home";

export default function App() {
  const [isLogged, setIsLogged] = useState(false);
  const [view, setView] = useState("menu"); // "menu" | "clima"

  // üëâ nuevo: guardamos el usuario logueado (viene del JSON)
  const [currentUser, setCurrentUser] = useState(null);

  const [city, setCity] = useState(null);
  const [current, setCurrent] = useState(null);

// Obtener ciudad basada en la geolocalizaci√≥n al entrar a "clima"
useEffect(() => {
  if (view !== "clima") return;

  navigator.geolocation.getCurrentPosition(
    (pos) => {
      const { latitude, longitude } = pos.coords;

      setCity({
        name: "Tu ubicaci√≥n",
        latitude,
        longitude
      });
    },
    (err) => {
      console.warn("No se pudo obtener ubicaci√≥n, usando ciudad default");
      setCity(CITIES[1]); // fallback
    }
  );
}, [view]);

  useEffect(() => {
    if (!isLogged || view !== "clima") return;

    let alive = true;
    (async () => {
      try {
        const data = await fetchCurrentWeather(city);
        if (alive) setCurrent(data);
      } catch {
        if (alive) setCurrent(null);
      }
    })();

    return () => {
      alive = false;
    };
  }, [isLogged, view, city.latitude, city.longitude]);

  // cuando el usuario elige una app en el men√∫
  const handleSelectApp = (app) => {
    if (app === "clima") {
      setView("clima");
    } else if (app === "app2") {
      window.open("https://ejemplo-app2.com", "_blank");
    } else if (app === "app3") {
      window.open("https://ejemplo-app3.com", "_blank");
    } else if (app === "app4") {
      window.open("https://ejemplo-app4.com", "_blank");
    }
  };

  return (
    <ThemeProvider>
      {/* 1Ô∏è‚É£ Si NO est√° logueado ‚Üí solo login */}
      {!isLogged && (
        <Login
          onLogin={(user) => {
            // user viene del JSON (foundUser)
            setCurrentUser(user);
            setIsLogged(true);
            setView("menu");
          }}
        />
      )}

      {/* 2Ô∏è‚É£ Logueado y en men√∫ ‚Üí 4 opciones */}
      {isLogged && view === "menu" && (
        <Home
          onSelectApp={handleSelectApp}
          // si quieres usar el nombre en Home:
          user={currentUser}
        />
      )}

      {/* 3Ô∏è‚É£ Logueado y en vista "clima" ‚Üí tu app de clima completa */}
      {isLogged && view === "clima" && (
        <>
          <Header city={city} onCityChange={setCity} />

          <main className="container row">
            <div className="card">
              <CurrentCard current={current} />
            </div>

            <div className="card">
              <h3 style={{ marginTop: 0 }}>Hoy</h3>
              <WeatherList city={city} />
            </div>

            <div className="card">
              <WeeklyForecast city={city} />
            </div>
          </main>
        </>
      )}
    </ThemeProvider>
  );
}
