// Open-Meteo: no API key required
export async function fetchHourlyWeather({ latitude, longitude, timezone = "auto" }) {
  const url = `https://api.open-meteo.com/v1/forecast?latitude=${latitude}&longitude=${longitude}&hourly=temperature_2m&forecast_days=1&timezone=${timezone}`;
  const res = await fetch(url);
  if (!res.ok) throw new Error("Failed to fetch weather");
  const data = await res.json();

  const times = data?.hourly?.time || [];
  const temps = data?.hourly?.temperature_2m || [];
  return times.map((iso, i) => ({
    iso,
    temp: temps[i],
  }));
}
