import React, { useEffect, useState } from "react";
import { fetchDailyWeather } from "../services/weather";

const iconFor = (code, isDay) => {
  if ([0].includes(code)) return isDay ? "â˜€ï¸" : "ðŸŒ™";
  if ([1, 2].includes(code)) return isDay ? "ðŸŒ¤ï¸" : "ðŸŒ™â˜ï¸";
  if ([3].includes(code)) return "â˜ï¸";
  if ([45, 48].includes(code)) return "ðŸŒ«ï¸";
  if ([61, 63, 65, 80, 81, 82].includes(code)) return "ðŸŒ§ï¸";
  if ([95, 96, 99].includes(code)) return "â›ˆï¸";
  return isDay ? "â˜€ï¸" : "ðŸŒ™";
};

const dayLabel = (iso, idx) => {
  if (idx === 0) return "Hoy";
  return new Date(iso).toLocaleDateString("es-MX", { weekday: "short" });
};

export default function WeeklyForecast({ city }) {
  const [days, setDays] = useState([]);
  const [loading, setLoading] = useState(true);
  const [err, setErr] = useState(null);

  useEffect(() => {
    let alive = true;
    setLoading(true);
    setErr(null);
    (async () => {
      try {
        const data = await fetchDailyWeather(city);
        if (alive) setDays(data);
      } catch (e) {
        if (alive) setErr(e?.message || "Error");
      } finally {
        if (alive) setLoading(false);
      }
    })();
    return () => { alive = false; };
  }, [city.latitude, city.longitude]);

  if (loading) return <div className="card">Cargando climaâ€¦</div>;
  if (err) return <div className="card" style={{ color: "crimson" }}>Error: {err}</div>;

  return (
    <div className="forecast-scroll">
      {days.map((d, i) => (
        <div key={d.date} className="forecast-item">
          <span className="day">{dayLabel(d.date, i)}</span>
          <span className="rain">ðŸ’§{d.precip ?? 0}%</span>
          <div className="icons">
            <span>{iconFor(d.code, true)}</span>
            <span>{iconFor(d.code, false)}</span>
          </div>
          <div className="temps">
            <span className="max">{Math.round(d.max)}Â°</span>
            <span className="min">{Math.round(d.min)}Â°</span>
          </div>
        </div>
      ))}
    </div>
  );
}
